import React, {Component} from 'react';
import Invitation from "./Invitation";
import './InvitationSection.css'
import soloInvitationImage from '../../assets/solo-image.jpg';
import groupInvitationImage from '../../assets/group-image.jpg';

function InvitationSection(props) {
    const soloStyle={
        'flex-direction': 'row'
    };
    const groupStyle={
        'flex-direction': 'row-reverse'
    };
    const soloInvitationTitle = 'Запрошую на індивідуальні консультації';
    const soloInvitationText = <nobr>Теми з якими я працюю:</nobr>+' стосунки в сім\'ї, парі, самооцінка та самоцінність, самореалізація, депресія, панічні атаки та страхи,\n' +
        '                                          психосоматичні симптоми, втрати, дитячий травматичний досвід, життєві ресурси, особисті кордони та інші.\n' +
        '                                          Проводжу супервізію для психологів та психотерапевтів';
    const groupInvitationTitle = 'Навчально-тренінгові та терапевтичні групи';
    const groupInvitationText = 'Беручи участь в групі можна отримати новий досвід взаємостосунків, віддавати та приймати підтримку, ділитись почуттями та думками.'+
    'Навчально-тренінгові групи спрямовані на отримання знань з психології, психотерапії які можна використати в житті,'+
        'в професійній діяльності, стосунках (в особистих і ділових). Терапевтичні групи спрямовані на отримання досвіду групової психотерапії, вирішення внутрішніх конфліктів';
        return (
            <div className="invitation-section">
                <div className="container">
                    <div className="invitation-section__inner">
                        <Invitation style={soloStyle} title={soloInvitationTitle} text={soloInvitationText} img={soloInvitationImage}/>
                        <Invitation style={groupStyle} title={groupInvitationTitle} text={groupInvitationText} img={groupInvitationImage}/>
                    </div>
                </div>
            </div>
        );
}

export default InvitationSection;
