import React, { Component } from 'react';
import './DropdownItem.css'
import {Link} from "react-scroll/modules";


class DropdownItem extends Component {
    constructor() {
        super();

        this.state = {
            showMenu: false,
        };

        this.showMenu = this.showMenu.bind(this);
        this.closeMenu = this.closeMenu.bind(this);
    }

    showMenu(event) {
        event.preventDefault();

        this.setState({ showMenu: true }, () => {
            document.addEventListener('click', this.closeMenu);
        });
    }

    closeMenu() {
        this.setState({ showMenu: false }, () => {
            document.removeEventListener('click', this.closeMenu);
        });
    }

    render() {
        return (
            <div className="header__navigation-link" onMouseEnter={this.showMenu} onMouseLeave={this.closeMenu}>
                    Консультації
                {
                    this.state.showMenu
                        ? (
                            <div className="custom-dropdown-menu">
                                <Link to="portfolio" smooth={true} className="header__navigation-link">Групові заняття</Link>
                                <Link to="portfolio" smooth={true} className="header__navigation-link">Індивідуальні консультації</Link>
                            </div>
                        )
                        : (
                            null
                        )
                }
            </div>
        );
    }
}

export default DropdownItem;
