import React from 'react';
import {Component} from "react";
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css'
import Header from './Components/Header/Header';
import Home from './Pages/Home/Home'
import Backdrop from "./Components/Backdrop/Backdrop";
import GroupLessons from './Pages/Event/GroupLessons(back-free)'
import SoloLessons from './Pages/Event/SoloLessons'
import SideDrawer from "./Components/SideDrawer/SideDrawer";
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom';

class App extends Component {
    state = {
        sideDrawerOpen: false
    };

    drawerToggleClickHandler = () => {
        this.setState((prevState) => {
            return {sideDrawerOpen: !prevState.sideDrawerOpen}
        });
    };

    backdropClickHandler = () => {
        this.setState({sideDrawerOpen: false})
    }

    render() {
        let backdrop;

        if (this.state.sideDrawerOpen) {
            backdrop = <Backdrop click={this.backdropClickHandler}/>
        }
        return (
            <Router>
                <Header drawerClickHandler={this.drawerToggleClickHandler} toggle={this.state.sideDrawerOpen}/>
                <SideDrawer show={this.state.sideDrawerOpen}/>
                {backdrop}
                <Switch>
                    <Route exact path="/" component={Home}/>
                    <Route exact path="/groupLessons" component={GroupLessons}/>
                    <Route exact path="/soloLessons" component={SoloLessons}/>
                </Switch>

            </Router>

        );
    }
}

export default App;
