import React, {Component} from 'react';
import groupPhoto from '../../assets/background.jpg';
import Button from '../Button/Button'
import './Group.css'
import SubTitle from "../SubTitle/SubTitle";
import SimpleText from "../SimpleText/SimpleText";
import GroupMoreInfo from './GroupMoreInfo'
class Group extends Component{
    constructor() {
        super();
        this.state = {
            show: false
        };
        this.showModal = this.showModal.bind(this);
        this.hideModal = this.hideModal.bind(this);
    }

    showModal = () => {
        this.setState({ show: true });
    };

    hideModal = () => {
        this.setState({ show: false });
    };
        render() {
            const groupSimpleTextStylr = {
                'text-align':'left'
            };
            return (
                <div className="group-container">
                    <div className="group-container-img">
                        <img src={props.image} width="100%" height="400px"/>
                    </div>
                    <div className="group-container-info">
                        <SubTitle text={props.title}/>

                        <div className="group-short-info-container">
                            <SimpleText text={props.location}/>
                            <SimpleText text={props.time}/>
                            <SimpleText text={props.date}/>

                        </div>
                        <div className="group-text">
                            <SimpleText style={groupSimpleTextStylr} text={props.text}/>
                        </div>
                        <div className="group-button-container">
                            <Button  text="Записатись"/>
                            <Button text="Детальніше"/>
                        </div>
                        <GroupMoreInfo show={this.state.show} handleClose={this.hideModal}>
                            <p>Modal</p>
                        </GroupMoreInfo>
                    </div>
                </div>

            );
        }
}

export default Group;
