import React, { Component } from 'react';
import './DropdownItem.css'


class DropdownItem extends Component {
    
    getInitialState() {
        return {
            showMenu: false
        };
    }
    onMouseEnterHandler() {
        this.setState({
            showMenu: true
        });
    }

    onMouseLeaveHandler() {
        this.setState({
            showMenu: false
        });
    }

    render() {
        return (
            <div className="header__navigation-link" onMouseLeave={this.onMouseLeaveHandler} onMouseEnter={this.onMouseEnterHandler}>
                    Show menu

                {
                    this.state.showMenu
                        ? (
                            <div className="custom-dropdown-menu">
                                <button> Menu item 1 </button>
                                <button> Menu item 2 </button>
                                <button> Menu item 3 </button>
                            </div>
                        )
                        : (
                            null
                        )
                }
            </div>
        );
    }
}

export default DropdownItem;
