import React, {Component} from 'react';
import Title from "../Title/Title";
import Motivation from "./Motivation";
import './MotivationSection.css'
import groupImage from '../../assets/group-consult-check1.jpg'
function MotivationSection(props) {
    const motivationTitle = 'Для кого підійдуть групові заннятя?';
    const motivationRightImageTitle  = 'Навчально-тренінгові групи';
    const motivationRightImageText  = <React.Fragment>
        <p className="motivation-check">Хто цікавиться психологією, психотерапією, особистісним розвитком.</p>
        <p className="motivation-check"> Бажає покращити свої стосунки з партнером, батьками та з дітьми.</p>
        <p className="motivation-check"> Прагне отримати базові знання з практичної психології.</p>
        <p className="motivation-check">Хто хоче зрозуміти краще себе та інших.</p> </React.Fragment>;
    const motivationLeftImageTitle  = 'Терапевтичні групи';
    const motivationLeftImageText  = <React.Fragment>
        <p className="motivation-check">Всім хто хоче знати як зрозуміло повідомляти про свої потреби іншим людям.</p>
        <p className="motivation-check"> Бажає покращити свої стосунки з партнером, батьками та з дітьми.</p>
        <p className="motivation-check"> Прагне отримати базові знання з практичної психології.</p>
        <p className="motivation-check">Хто хоче зрозуміти краще себе та інших.</p> </React.Fragment>;
        return (
            <div className="motivation-section">
                <div className="container">
                    <Title text={motivationTitle}/>
                    <div className="motivation-section__inner">
                        <Motivation text={motivationRightImageText} title={motivationRightImageTitle} img={groupImage}/>
                        <Motivation text={motivationRightImageText} title={motivationLeftImageTitle} img={groupImage}/>
                    </div>
                </div>
            </div>
        );

}

export default MotivationSection;
